		#region Data Structure
		private enum e_col_Number{
			DON_VI_GIANG_DAY = 3
,TEN_MON_HOC = 2
,MA_MON_HOC = 1

		}			
		#endregion

		#region Members
		ITransferDataRow m_obj_trans;		
